// Mostrar el menú contextual
function showmenu(e, content) {
    e.preventDefault(); // Prevenir el menú contextual predeterminado

    // Obtener el elemento del menú
    const menu = document.getElementById("popmenu");
    if (!menu) return;

    // Establecer el contenido dinámico del menú
    menu.innerHTML = content;

    // Obtener dimensiones del menú, ratón, ventana y desplazamiento
    const { clientX: mouseX, clientY: mouseY } = e; // Posición del ratón en la ventana
    const { innerWidth: windowWidth, innerHeight: windowHeight } = window; // Tamaño de la ventana
    const { scrollX, scrollY } = window; // Desplazamiento del documento
    const { offsetWidth: menuWidth, offsetHeight: menuHeight } = menu; // Tamaño del menú

    // Calcular posición ajustada si el menú excede los bordes
    let top = mouseY + scrollY; // Posición vertical del menú
    let left = mouseX + scrollX; // Posición horizontal del menú

    // Ajustar posición si el menú se sale del borde inferior
    if (top + menuHeight > windowHeight + scrollY) {
        top = windowHeight + scrollY - menuHeight; // Mostrar hacia arriba
    }

    // Ajustar posición si el menú se sale del borde derecho
    if (left + menuWidth > windowWidth + scrollX) {
        left = windowWidth + scrollX - menuWidth; // Mostrar hacia la izquierda
    }

    // Ajustar posición si el menú se sale del borde superior
    if (top < scrollY) {
        top = scrollY; // Mostrar hacia abajo
    }

    // Ajustar posición si el menú se sale del borde izquierdo
    if (left < scrollX) {
        left = scrollX; // Mostrar hacia la derecha
    }

    // Aplicar posición y mostrar el menú
    menu.style.top = `${top}px`;
    menu.style.left = `${left}px`;
    menu.style.visibility = "visible";
}

// Ocultar el menú contextual
function hidemenu() {
    const menu = document.getElementById("popmenu");
    if (menu) {
        menu.style.visibility = "hidden";
    }
}

// Inicializar el comportamiento del menú contextual
document.addEventListener("click", hidemenu); // Ocultar el menú al hacer clic en cualquier parte


